    line.includes('/tcp') && line.includes('open')
  );

  portLines.forEach(line => {
    const parts = line.trim().split(/\s+/);
    if (parts.length >= 3) {
      const portInfo = parts[0].split('/');
      const port = portInfo[0];
      const protocol = portInfo[1] || 'tcp';
      const state = parts[1];
      const service = parts[2] || 'unknown';

      if (state === 'open') {
        services[port] = {
          port: parseInt(port),
          protocol: protocol,
          state: state,
          service: service,
          version: parts.slice(3).join(' ') || ''
        };
      }
    }
  });

  serviceEnumResult = {
    target: target,
    timestamp: new Date().toISOString(),
    open_ports: Object.keys(services).map(p => parseInt(p)),
    services: services,
    scan_info: {
      timestamp: Date.now(),
      intensity: 'medium'
    }
  };

