  } 
}];

// Procesar reporte final de evidencias
const reportResult = items[0].json;
const exploitationData = reportResult.exploitation_result || {};

console.log('Procesando reporte de evidencias:', JSON.stringify(reportResult, null, 2));

// Analizar informaciÃ³n del reporte de evidencias
let reportInfo = {
  report_generated: false,
  html_report: null,
  json_report: null,
  evidence_summary: {}
};

if (reportResult.stdout) {
  const output = reportResult.stdout;

  // Extraer rutas de archivos de reporte
  const htmlMatch = output.match(/HTML: ([^\n]+)/);
  const jsonMatch = output.match(/JSON: ([^\n]+)/);

  if (htmlMatch) {
    reportInfo.html_report = htmlMatch[1];
    reportInfo.report_generated = true;
  }

  if (jsonMatch) {
    reportInfo.json_report = jsonMatch[1];
  }

  // Extraer mÃ©tricas del reporte
  const evidenceMatch = output.match(/Evidence Files Analyzed: (\d+)/);
  const vulnMatch = output.match(/Total Vulnerabilities: (\d+)/);
  const riskMatch = output.match(/Risk Level: (\w+)/);
  const servicesMatch = output.match(/Services Tested: (\d+)/);
  const credsMatch = output.match(/Credentials Found: (\d+)/);

  reportInfo.evidence_summary = {
    evidence_files_analyzed: evidenceMatch ? parseInt(evidenceMatch[1]) : 0,
    total_vulnerabilities: vulnMatch ? parseInt(vulnMatch[1]) : 0,
    risk_level: riskMatch ? riskMatch[1] : 'UNKNOWN',
    services_tested: servicesMatch ? parseInt(servicesMatch[1]) : 0,
    credentials_found: credsMatch ? parseInt(credsMatch[1]) : 0
  };
}

// Generar identificadores del reporte
const timestamp = new Date().toISOString();
const reportId = `AUDIT_${(exploitationData.target || reportResult.target || "unknown").replace(/[^0-9]/g, '').substring(0, 14)}`;

// Construir reporte final consolidado
const finalReport = {
  report_id: reportId,
  timestamp: timestamp,
  target: exploitationData.target || reportResult.target,

  executive_summary: {
    assessment_type: exploitationData.exploitation_type || 'comprehensive',
    assessment_completed: true,
    evidence_collected: reportInfo.report_generated,
    total_vulnerabilities: reportInfo.evidence_summary.total_vulnerabilities || exploitationData.vulnerabilities_found || 0,
    risk_level: reportInfo.evidence_summary.risk_level || (exploitationData.exploits_successful > 0 ? 'HIGH' : exploitationData.vulnerabilities_found > 0 ? 'MEDIUM' : 'LOW'),
    services_tested: reportInfo.evidence_summary.services_tested || exploitationData.services_tested || 0,
    credentials_discovered: reportInfo.evidence_summary.credentials_found || exploitationData.credentials_discovered || 0
  },

  technical_summary: {
    enumeration_completed: true,
    exploitation_completed: exploitationData.success !== false,
    evidence_files_collected: reportInfo.evidence_summary.evidence_files_analyzed || 0,
    tools_used: exploitationData.tools_used || ['nmap', 'nuclei'],
    ...(exploitationData.exploitation_type?.includes('active_directory') && {
      ad_environment_detected: exploitationData.is_domain_controller || false,
      autobloody_successful: exploitationData.autobloody_success || false,
      users_enumerated: exploitationData.users_enumerated || 0,
      groups_enumerated: exploitationData.groups_enumerated || 0
    })
  },

  evidence_locations: {
    evidence_directory: exploitationData.evidence_location || `results/evidence/${exploitationData.target}`,
    html_report: reportInfo.html_report,
    json_report: reportInfo.json_report,
    ...(exploitationData.ad_evidence_location && {
      ad_evidence_directory: exploitationData.ad_evidence_location
    })
  },

  recommendations: [
    reportInfo.evidence_summary.total_vulnerabilities > 0 ? 
      `URGENTE: Remediar ${reportInfo.evidence_summary.total_vulnerabilities} vulnerabilidades identificadas` : 
      'Mantener monitoreo de seguridad continuo',
    exploitationData.credentials_discovered > 0 ?
      'Revisar y cambiar credenciales expuestas inmediatamente' : 
      'Implementar polÃ­ticas de contraseÃ±as robustas',
    exploitationData.exploitation_type?.includes('active_directory') ? 
      'Revisar configuraciÃ³n de Active Directory y polÃ­ticas de dominio' : 
      'Implementar segmentaciÃ³n de red y principio de menor privilegio',
    'Realizar evaluaciones de seguridad periÃ³dicas',
    'Implementar sistema de monitoreo y detecciÃ³n de intrusiones'
  ].filter(Boolean),

  workflow_status: 'COMPLETED_WITH_REPORT',
  completion_time: timestamp
};

console.log('Reporte final generado:', JSON.stringify(finalReport, null, 2));
console.log('Reporte HTML:', reportInfo.html_report);
console.log('Nivel de riesgo:', finalReport.executive_summary.risk_level);
console.log('Vulnerabilidades encontradas:', finalReport.executive_summary.total_vulnerabilities);

return [{ json: finalReport }];
